import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const UpdateEmployee = () => {
    const { eid } = useParams();
    const [employee, setEmployee] = useState({
        firstname: "",
        lastName: "",
        email: "",
    });

    const navigate = useNavigate();

    useEffect(() => {
        // Fetch the existing employee data to pre-fill the form
        axios.get(`http://localhost:5000/api/v1/emp/employees/${eid}`)
            .then(result => {
                setEmployee(result.data);  // Assuming your backend returns the employee data
            })
            .catch(err => console.log(err));
    }, [eid]);

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.put(`http://localhost:5000/api/v1/emp/employees/${eid}`, employee)
            .then(result => {
                if(result.data.status) {
                    navigate('/employee');
                } else {
                    alert(result.data.error);
                }
            })
            .catch(err => console.log(err));
    };

    const handleCancel = () => {
        // Handle cancel logic, if needed
        navigate('/employee');
    };

    return (
        <div >
            <div>
                <h3>Update Employee</h3>
                <form  onSubmit={handleSubmit}>
                    <div>
                        <label>
                            First Name
                        </label>
                        <input
                            type="text"
                            id="inputFirstName"
                            placeholder="Enter First Name"
                            value={employee.firstName}
                            onChange={(e) =>
                                setEmployee({ ...employee, firstName: e.target.value })
                            }
                        />
                    </div>
                    <div className="col-12">
                        <label>
                            Last Name
                        </label>
                        <input
                            type="text"
                            
                            id="inputLastName"
                            placeholder="Enter Last Name"
                            value={employee.lastName}
                            onChange={(e) =>
                                setEmployee({ ...employee, lastName: e.target.value })
                            }
                        />
                    </div>
                    <div className="col-12">
                        <label>
                            Email
                        </label>
                        <input
                            type="email"
                            id="inputEmail"
                            placeholder="Enter Email"
                            autoComplete="off"
                            value={employee.email}
                            onChange={(e) =>
                                setEmployee({ ...employee, email: e.target.value })
                            }
                        />
                    </div>
                    <div className="col-12">
                        <button type="submit">
                            Update Employee
                        </button>
                        <button type="button" onClick={handleCancel}>
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UpdateEmployee;
