import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./UpdateEmployee.css"; // Import the CSS file

const UpdateEmployee = () => {
    const { eid } = useParams();
    const [employee, setEmployee] = useState({
        firstName: "",
        lastName: "",
        email: "",
    });

    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:5000/api/v1/emp/employees/${eid}`)
            .then(result => {
                setEmployee(result.data);
            })
            .catch(err => console.log(err));
    }, [eid]);

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.put(`http://localhost:5000/api/v1/emp/employees/${eid}`, employee)
            .then(result => {
                if(result.data.status) {
                    navigate('/employee');
                } else {
                    alert(result.data.error);
                }
            })
            .catch(err => console.log(err));
    };

    const handleCancel = () => {
        navigate('/employee');
    };

    return (
        <div className="container">
            <div className="form-wrapper">
                <h3 className="header">Update Employee</h3>
                <form className="form" onSubmit={handleSubmit}>
                    {/* ... your form inputs ... */}
                    <div className="button-container">
                        <button className="submit-button" type="submit">
                            Update Employee
                        </button>
                        <button className="cancel-button" type="button" onClick={handleCancel}>
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UpdateEmployee;
